INSERT INTO AUTORES (aut_nome, aut_bio, aut_foto) VALUES

('Bernardo Guimarães', 'Bernardo Guimarães (1825-1884) foi um escritor e poeta brasileiro, conhecido principalmente por suas obras de cunho nacionalista e regionalista, como o romance "O Primo Basílio’.', 'foto_bernardo_guimaraes.jpg'),

('Machado de Assis', 'Machado de Assis (1839-1908) é considerado um dos maiores escritores da literatura brasileira. Com uma obra vasta, destacou-se como romancista, dramaturgo, poeta e crítico literário. Suas obras incluem "Dom Casmurro" e "Memórias Póstumas de Brás Cubas".', 'foto_machado_assis.jpg'),

('Aluísio Azevedo', 'Aluísio Azevedo (1857-1913) foi um escritor naturalista brasileiro. Suas obras mais conhecidas são "O Cortiço" e "Casa de Pensão", abordando a vida urbana e as desigualdades sociais do Brasil.', 'foto_aluisio_azevendo.jpg'),

('Olavo Bilac', 'Olavo Bilac (1865-1918) foi um poeta, jornalista e professor brasileiro, um dos principais nomes do Parnasianismo. Sua obra poética, com destaque para "Poesias", reflete o rigor formal e a beleza estética.', 'foto_olavo_bilac.jpg'),

('Cruz e Sousa', 'Cruz e Sousa (1861-1898) foi um poeta brasileiro, um dos maiores nomes do simbolismo no Brasil. Sua obra "Broquéis" é uma das mais importantes da literatura simbolista brasileira.', 'foto_cruz_sousa.jpg'),

('Monteiro Lobato', 'Monteiro Lobato (1882-1948) foi um escritor, editor e ativista brasileiro. Conhecido por suas obras infantis, especialmente a série do Sítio do Picapau Amarelo, ele também foi um importante defensor da educação e do desenvolvimento cultural no Brasil.', 'foto_monteiro_lobato.jpg'),

('Cora Coralina', 'Cora Coralina (1889-1965) foi uma escritora brasileira, conhecida por suas poesias e contos sobre o interior de Goiás e a vida simples do povo brasileiro, especialmente em sua obra "Poemas dos Becos de Goiás".', 'foto_cora_coralina.jpg'),

('Mário de Andrade', 'Mário de Andrade (1893-1945) foi um dos principais escritores e musicólogos do modernismo brasileiro. Sua obra mais famosa, "Macunaíma", reflete a busca por uma identidade nacional no Brasil.', 'foto_mario_andrade.jpg'),

('Carlos Drummond de Andrade', 'Carlos Drummond de Andrade (1902-1987) foi um dos maiores poetas brasileiros, marcando a literatura moderna com obras como "Alguma Poesia" e "A Rosa do Povo".', 'foto_carlos_drummond.jpg'),

('Mario Quintana', 'Mario Quintana (1906-1994) foi um poeta brasileiro, conhecido por seu estilo simples, lírico e reflexivo. Sua obra é marcada pela delicadeza e pela observação do cotidiano, como em "O Poema de Sete Faces".', 'foto_mario_quintana.jpg'),
('Jorge Amado', 'Jorge Amado (1912-2001) foi um escritor brasileiro, um dos maiores representantes da literatura brasileira, com obras como "Gabriela, Cravo e Canela" e "Capitães da Areia", que retratam a vida na Bahia.', 'foto_jorge_amado.jpg'),

('Nelson Rodrigues', 'Nelson Rodrigues (1912-1980) foi um dramaturgo, jornalista e escritor brasileiro, conhecido por suas peças teatrais e novelas que abordam temas como a moralidade, o desejo e a hipocrisia social, como em "Vestido de Noiva".', 'foto_nelson_rodrigues.jpg'),

('João Guimarães Rosa', 'João Guimarães Rosa (1908-1967) foi um escritor e diplomata brasileiro, conhecido principalmente por sua obra "Grande Sertão: Veredas", que revolucionou a literatura brasileira com seu estilo inovador.', 'foto_joao_guimaraes_rosa.jpg'),

('Carolina Maria de Jesus', 'Carolina Maria de Jesus (1914-1977) foi uma escritora brasileira, famosa por sua obra "Quarto de Despejo", um diário que retrata a vida de uma mulher pobre em São Paulo durante os anos 1950.', 'foto_carolina_maria_jesus.jpg'),

('Manoel de Barros', 'Manoel de Barros (1916-2014) foi um poeta brasileiro, conhecido por sua linguagem única e poética que lida com temas do cotidiano e da natureza. Sua obra inclui livros como "Poemas Rupestres" e "O Livro das Ignorãças".', 'foto_manoel_barros.jpg'),

('J. K. Rowling', 'J. K. Rowling (1965-X) Escritora britânica famosa pela série Harry Potter, uma das sagas literárias mais vendidas da história.', 'jk_rowling.jpg'),
('Jack London', 'Jack London (1876-1916) Escritor e jornalista norte-americano conhecido por seus romances de aventura, como O Chamado da Floresta.', 'jack_london.jpg'),
('Jeff Kinney', 'Jeff Kinney (1971-X) Cartunista e escritor americano, criador da popular série infantojuvenil Diário de um Banana.', 'jeff_kinney.jpg'),
('Júlio Verne', 'Júlio Verne (1828-1905) foi um escritor francês pioneiro da ficção científica, conhecido por obras como "Vinte Mil Léguas Submarinas", "A Volta ao Mundo em 80 Dias" e "Viagem ao Centro da Terra". Suas histórias visionárias previram avanços científicos e tecnológicos muito antes de seu tempo.', 'julio_verne.jpg'),
('Lewis Carroll', 'Lewis Carroll (1832-1898) foi o pseudônimo de Charles Lutwidge Dodgson, um escritor, matemático, lógico e fotógrafo britânico. Ele é mais conhecido por seu livro "Alice no País das Maravilhas" (1865) e sua sequência "Através do Espelho e o que Alice Encontrou Por Lá" (1871). Suas obras revolucionaram a literatura infantil com elementos de lógica, fantasia e jogos de palavras. Além da escrita, Dodgson era professor de matemática na Universidade de Oxford e publicou diversos trabalhos acadêmicos na área.', 'lewis_carroll.jpg'),

('Taylor Jenkins Reid', 'Taylor Jenkins Reid (1983-X) é uma escritora norte-americana famosa por seus romances que exploram a fama, o amor e a complexidade dos relacionamentos humanos. Seus livros mais conhecidos incluem "Os Sete Maridos de Evelyn Hugo" (2017), "Daisy Jones & The Six" (2019) e "Malibu Renasce" (2021). Antes de se tornar escritora, trabalhou na indústria cinematográfica, o que influenciou sua abordagem narrativa sobre celebridades e cultura pop. Seu trabalho já foi adaptado para séries e filmes.', 'taylor_jenkins_reid.jpg'),  

('Rebecca Yarros', 'Rebecca Yarros (1981-X) é uma autora best-seller norte-americana conhecida por seus romances contemporâneos e de fantasia. Seu maior sucesso é "Quarta Asa" (2023), primeiro livro da série "O Empíreo", que rapidamente se tornou um fenômeno mundial. Antes de se aventurar na fantasia, Rebecca escreveu romances contemporâneos, frequentemente abordando temas como amor, trauma e superação. Além de escritora, é mãe de seis filhos e atua em projetos sociais voltados para veteranos militares e adoção.', 'rebecca_yarros.jpg'),  

('Tatielle Katluryn', 'Tatielle Katluryn (1996-X) é uma escritora brasileira que vem conquistando leitores com suas histórias envolventes e personagens cativantes. Seus livros transitam entre os gêneros de romance, fantasia e drama, misturando emoções intensas e tramas bem construídas. Seu talento para criar narrativas apaixonantes e reflexivas a colocou como uma das promissoras vozes da literatura nacional contemporânea.', 'tatielle_katluryn.jpg'),  

('Colleen Hoover', 'Colleen Hoover (1979-X) é uma escritora norte-americana de romances contemporâneos e new adult, conhecida por suas histórias emocionantes e envolventes. Seu livro mais famoso, "É Assim Que Acaba" (2016), aborda temas como relacionamentos abusivos e resiliência feminina. Outras obras de destaque incluem "Verity" (2018), um thriller psicológico, e "Todas as Suas (Im)perfeições" (2018). Hoover começou publicando seus livros de forma independente antes de se tornar uma das autoras mais vendidas do mundo, com milhões de cópias comercializadas.', 'colleen_hoover.jpg'),  

('Dav Pilkey', 'Dav Pilkey (1966-X) é um escritor e ilustrador norte-americano, conhecido principalmente por criar a série "As Aventuras do Capitão Cueca" e "Homem-Cão". Diagnosticado com TDAH e dislexia na infância, ele encontrou nos desenhos e na escrita uma forma de se expressar, o que influenciou seu estilo irreverente e humorístico. Seus livros, que misturam quadrinhos e literatura infantil, são elogiados por incentivar o gosto pela leitura em crianças e jovens. Pilkey também é defensor da importância da criatividade e da liberdade de expressão na literatura infantil.', 'dav_pilkey.jpg');  

INSERT INTO AUTORES (aut_nome, aut_bio, aut_foto) VALUES 
('Globo Livros', 'Globo Livros é uma editora brasileira que publica uma ampla variedade de livros, incluindo ficção, não ficção, biografias e literatura infantojuvenil.', 'foto_globo_livros.jpg'),  
('Cressida Cowell','Cressida Cowell é uma escritora e ilustradora britânica, conhecida principalmente pela série de livros *Como Treinar o Seu Dragão*, que inspirou os filmes animados da DreamWorks. Nascida em 15 de abril de 1966, ela cresceu em Londres, mas passou parte da infância em uma ilha remota na Escócia, o que influenciou suas histórias sobre vikings e dragões. Além dessa série de sucesso, também escreveu *Os Wizards of Once*. Cowell já foi a Childrens Laureate do Reino Unido (2019-2022), cargo dedicado a promover a leitura infantil. ','foto_Cressida_Cowell.png'),
 
('Margaret Atwood','Margaret Atwood é uma escritora, poetisa e ensaísta canadense, nascida em 18 de novembro de 1939. Reconhecida por suas obras de ficção especulativa, ela é mais famosa pelo romance *O Conto da Aia* (*The Handmaid’s Tale*), que inspirou uma série de TV de grande sucesso. Sua escrita aborda temas como feminismo, distopia e questões ambientais. Ao longo de sua carreira, Atwood recebeu diversos prêmios literários e se tornou uma das vozes mais influentes da literatura contemporânea.','foto_Margaret_Atwood') 
